var e=require("assert"),t=require("../test")(module),r=require("../../lib/html"),n=require("../../lib/writers");function generateString(e){var t=n.writer();return r.write(t,[e]),t.asString()}t("text is HTML escaped",(function(){e.equal(generateString(r.text("<>&")),"&lt;&gt;&amp;")})),t("double quotes outside of attributes are not escaped",(function(){e.equal(generateString(r.text('"')),'"')})),t("element attributes are HTML escaped",(function(){e.equal(generateString(r.freshElement("p",{x:"<"})),'<p x="&lt;"></p>')})),t("double quotes inside attributes are escaped",(function(){e.equal(generateString(r.freshElement("p",{x:'"'})),'<p x="&quot;"></p>')})),t("element children are written",(function(){e.equal(generateString(r.freshElement("p",{},[r.text("Hello")])),"<p>Hello</p>")}));