import{SimpleTourManager as t}from"./simple-tour.js";export const SimpleTourFinder=function(e){return class extends e{constructor(){super(),this.tourName="default"}connectedCallback(){super.connectedCallback&&super.connectedCallback(),super.firstUpdated||this.discoverSimpleTourStops()}disconnectedCallback(){super.disconnectedCallback()}firstUpdated(t){super.firstUpdated&&super.firstUpdated(t),this.discoverSimpleTourStops()}discoverSimpleTourStops(){this.shadowRoot.querySelectorAll("[data-simple-tour-stop]").forEach((e=>{try{let r="";e.getAttribute("data-stop-title")&&e.getAttribute(e.getAttribute("data-stop-title"))?r=e.getAttribute(e.getAttribute("data-stop-title")):e.querySelector("[data-stop-title]")&&(r=e.querySelector("[data-stop-title]").innerHTML);let o=e.querySelector("[data-stop-content]").innerHTML?e.querySelector("[data-stop-content]").innerHTML:"";t.createTourStop(this.tourName,e,r,o,"live")}catch(t){console.warn(t)}}))}}};