export const FutureTerminalTextLiteSuper=function(t){return class extends t{static get styles(){}constructor(){super(),this.red=!1,this.fadein=!1,this.glitch=!1,this.glitchMax=5,this.glitchDuration=50}async _doGlitch(){const t=this.innerHTML,r=Math.floor(Math.floor(Math.random()*t.length/10)+t.length/20)+1,i=Math.floor(Math.random()*this.glitchMax)+10;for(let e=0;e<i;e++){let i=t;for(let t=0;t<r;t++)i=this._scramble(i);this.innerHTML=i,await this._wait(this.glitchDuration)}this.innerHTML=t}_scramble(t){const r=Math.floor(Math.random()*Math.floor(t.length-1)),i=Math.floor(100*Math.random());return t.substring(0,r)+String.fromCharCode(i)+t.substring(r+1)}_wait(t){return new Promise((r=>setTimeout(r,t)))}}};